#Posições do padrão no genoma:

def pattern_matching(pattern, genome):
    positions = [] #lista onde serão armazenadas as posições onde o padrão ocorre no genoma
    pattern_length = len(pattern)

    for i in range(len(genome) - pattern_length + 1):
        if genome[i:i + pattern_length] == pattern:
            positions.append(i) 
    return positions

# Exemplo de uso:
pattern = "ACTCGGGAC"
genome = ""

positions = pattern_matching(pattern, genome)

print(" ".join(map(str, positions)))
